package com.sajansthapit.authserver.authclient.models;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import java.time.Instant;@Entity@Table(name = "authentication_method")public class AuthenticationMethod {    @Id    private String id;    @Column(name = "method_name")    private String methodName;    @CreationTimestamp    @Column(name = "created_at")    private Instant createdAt;    @UpdateTimestamp    @Column(name = "updated_at")    private Instant updatedAt;    public AuthenticationMethod() {    }    public AuthenticationMethod(String id, String methodName, Instant createdAt, Instant updatedAt) {        this.id = id;        this.methodName = methodName;        this.createdAt = createdAt;        this.updatedAt = updatedAt;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getMethodName() {        return methodName;    }    public void setMethodName(String methodName) {        this.methodName = methodName;    }    public Instant getCreatedAt() {        return createdAt;    }    public void setCreatedAt(Instant createdAt) {        this.createdAt = createdAt;    }    public Instant getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(Instant updatedAt) {        this.updatedAt = updatedAt;    }}