package com.sajansthapit.authserver.authclient.models;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import java.time.Instant;@Entity@Table(name = "auth_client_scope")public class Scope {    @Id    private String id;    @Column(name = "scope_name")    private String scopeName;    @CreationTimestamp    @Column(name = "created_at")    private Instant createdAt;    @UpdateTimestamp    @Column(name = "updated_at")    private Instant updatedAt;    public Scope() {    }    public Scope(String id, String scopeName, Instant createdAt, Instant updatedAt) {        this.id = id;        this.scopeName = scopeName;        this.createdAt = createdAt;        this.updatedAt = updatedAt;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getScopeName() {        return scopeName;    }    public void setScopeName(String scopeName) {        this.scopeName = scopeName;    }    public Instant getCreatedAt() {        return createdAt;    }    public void setCreatedAt(Instant createdAt) {        this.createdAt = createdAt;    }    public Instant getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(Instant updatedAt) {        this.updatedAt = updatedAt;    }}