package com.sajansthapit.authserver.authclient.service;import com.sajansthapit.authserver.authclient.models.AuthClient;public interface IAuthClientService {    /**     * Method to find AuthClient by id     *     * @param id String     * @return AuthClient or else throw EntityNotFoundException     */    AuthClient getById(String id);    /**     * Method to find AuthClient by clientId     *     * @param clientId String     * @return AuthClient or else throw EntityNotFoundException     */    AuthClient getByClientId(String clientId);}