package com.sajansthapit.authserver.authclient.service.bootstrap;import com.sajansthapit.authserver.authclient.models.AuthenticationMethod;import com.sajansthapit.authserver.authclient.repository.AuthenticationMethodRepository;import com.sajansthapit.authserver.config.bootstrap.service.BootstrapData;import org.springframework.stereotype.Service;import java.util.List;import java.util.UUID;@Servicepublic class AuthenticationMethodBootstrapService implements BootstrapData<List<String>> {    private final AuthenticationMethodRepository authenticationMethodRepository;    public AuthenticationMethodBootstrapService(AuthenticationMethodRepository authenticationMethodRepository) {        this.authenticationMethodRepository = authenticationMethodRepository;    }    @Override    public void bootstrap(List<String> authenticationMethods) {        authenticationMethods.forEach(method -> {            AuthenticationMethod authenticationMethod = new AuthenticationMethod();            authenticationMethod.setId(UUID.randomUUID().toString());            authenticationMethod.setMethodName(method);            authenticationMethodRepository.save(authenticationMethod);        });    }}