package com.sajansthapit.authserver.authclient.service.bootstrap;import com.sajansthapit.authserver.authclient.models.AuthorizationGrantType;import com.sajansthapit.authserver.authclient.repository.AuthorizationGrantTypeRepository;import com.sajansthapit.authserver.config.bootstrap.service.BootstrapData;import org.springframework.stereotype.Service;import java.util.List;import java.util.UUID;@Servicepublic class AuthorizationGrantTypeBootstrapService implements BootstrapData<List<String>> {    private final AuthorizationGrantTypeRepository authorizationGrantTypeRepository;    public AuthorizationGrantTypeBootstrapService(AuthorizationGrantTypeRepository authorizationGrantTypeRepository) {        this.authorizationGrantTypeRepository = authorizationGrantTypeRepository;    }    @Override    public void bootstrap(List<String> authorizationGrantTypes) {        authorizationGrantTypes.forEach(grantType -> {            AuthorizationGrantType authorizationGrantType = new AuthorizationGrantType();            authorizationGrantType.setId(UUID.randomUUID().toString());            authorizationGrantType.setGrantType(grantType);            authorizationGrantTypeRepository.save(authorizationGrantType);        });    }}