package com.sajansthapit.authserver.authclient.service.bootstrap;import com.sajansthapit.authserver.authclient.models.Scope;import com.sajansthapit.authserver.authclient.repository.ScopeRepository;import com.sajansthapit.authserver.config.bootstrap.service.BootstrapData;import org.springframework.stereotype.Service;import java.util.List;import java.util.UUID;@Servicepublic class ScopeBootstrapService implements BootstrapData<List<String>> {    private final ScopeRepository scopeRepository;    public ScopeBootstrapService(ScopeRepository scopeRepository) {        this.scopeRepository = scopeRepository;    }    @Override    public void bootstrap(List<String> scopes) {        scopes.forEach(scope -> {            Scope saveScope = new Scope();            saveScope.setId(UUID.randomUUID().toString());            saveScope.setScopeName(scope);            scopeRepository.save(saveScope);        });    }}