package com.sajansthapit.authserver.authclient.service.impl;import com.sajansthapit.authserver.authclient.models.AuthClient;import com.sajansthapit.authserver.authclient.repository.AuthClientRepository;import com.sajansthapit.authserver.authclient.service.IAuthClientService;import jakarta.persistence.EntityNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class AuthClientService implements IAuthClientService {    private final AuthClientRepository authClientRepository;    public AuthClientService(AuthClientRepository authClientRepository) {        this.authClientRepository = authClientRepository;    }    @Override    public AuthClient getById(String id) {        return authClientRepository.findById(id)                .orElseThrow(() -> new EntityNotFoundException("Auth client with id " + id + " not found"));    }    @Override    public AuthClient getByClientId(String clientId) {        return authClientRepository.findByClientId(clientId)                .orElseThrow(() -> new EntityNotFoundException("Auth client with clientId " + clientId + " not found"));    }}