package com.sajansthapit.authserver.authclient.service.impl;import com.sajansthapit.authserver.authclient.models.AuthenticationMethod;import com.sajansthapit.authserver.authclient.repository.AuthenticationMethodRepository;import com.sajansthapit.authserver.authclient.service.IAuthenticationMethodService;import jakarta.persistence.EntityNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class AuthenticationMethodService implements IAuthenticationMethodService {    private final AuthenticationMethodRepository authenticationMethodRepository;    public AuthenticationMethodService(AuthenticationMethodRepository authenticationMethodRepository) {        this.authenticationMethodRepository = authenticationMethodRepository;    }    @Override    public AuthenticationMethod getByMethodName(String methodName) {        return authenticationMethodRepository.findByMethodName(methodName)                .orElseThrow(() -> new EntityNotFoundException("AuthenticationMethod with name " + methodName + " not found"));    }}