package com.sajansthapit.authserver.authclient.service.impl;import com.sajansthapit.authserver.authclient.models.AuthorizationGrantType;import com.sajansthapit.authserver.authclient.repository.AuthorizationGrantTypeRepository;import com.sajansthapit.authserver.authclient.service.IAuthorizationGrantTypeService;import jakarta.persistence.EntityNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class AuthorizationGrantTypeService implements IAuthorizationGrantTypeService {    private final AuthorizationGrantTypeRepository authorizationGrantTypeRepository;    public AuthorizationGrantTypeService(AuthorizationGrantTypeRepository authorizationGrantTypeRepository) {        this.authorizationGrantTypeRepository = authorizationGrantTypeRepository;    }    @Override    public AuthorizationGrantType getByGrantType(String grantType) {        return authorizationGrantTypeRepository.findByGrantType(grantType)                .orElseThrow(() -> new EntityNotFoundException("AuthorizationGrantType  with " + grantType + " not found"));    }}