package com.sajansthapit.authserver.authclient.service.impl;import com.sajansthapit.authserver.authclient.models.Scope;import com.sajansthapit.authserver.authclient.repository.ScopeRepository;import com.sajansthapit.authserver.authclient.service.IScopeService;import jakarta.persistence.EntityNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class ScopeService implements IScopeService {    private final ScopeRepository scopeRepository;    public ScopeService(ScopeRepository scopeRepository) {        this.scopeRepository = scopeRepository;    }    @Override    public Scope getByScopeName(String scopeName) {        return scopeRepository.findByScopeName(scopeName)                .orElseThrow(() -> new EntityNotFoundException("Scope with name " + scopeName + " not found"));    }}