package com.sajansthapit.authserver.authuser.model;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import java.time.Instant;@Entity@Table(name = "auth_role")public class AuthRole{    @Id    private String id;    @Column(name = "name")    private String name;    @CreationTimestamp    @Column(name = "created_at")    private Instant createdAt;    @UpdateTimestamp    @Column(name = "updated_at")    private Instant updatedAt;    public AuthRole() {    }    public AuthRole(String id, String name, Instant createdAt, Instant updatedAt) {        this.id = id;        this.name = name;        this.createdAt = createdAt;        this.updatedAt = updatedAt;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Instant getCreatedAt() {        return createdAt;    }    public void setCreatedAt(Instant createdAt) {        this.createdAt = createdAt;    }    public Instant getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(Instant updatedAt) {        this.updatedAt = updatedAt;    }}