package com.sajansthapit.authserver.authuser.model;import jakarta.persistence.*;import org.hibernate.annotations.CreationTimestamp;import org.hibernate.annotations.UpdateTimestamp;import java.time.Instant;import java.util.HashSet;import java.util.Set;@Entity@Table(name = "auth_user")public class AuthUser {    @Id    private String id;    @Column(name = "full_name")    private String fullName;    @Column(name = "email")    private String email;    @Column(name = "username")    private String username;    @Column(name = "password")    private String password;    @Column(name = "account_non_expired")    private boolean accountNonExpired;    @Column(name = "account_non_locked")    private boolean accountNonLocked;    @Column(name = "credential_non_expired")    private boolean credentialNonExpired;    @Column(name = "enabled")    private boolean enabled;    @CreationTimestamp    @Column(name = "created_at")    private Instant createdAt;    @UpdateTimestamp    @Column(name = "updated_at")    private Instant updatedAt;    @ManyToMany(fetch = FetchType.LAZY)    @JoinTable(name = "auth_user_roles",    joinColumns = @JoinColumn(name = "auth_user_id", referencedColumnName = "id"))    private Set<AuthRole> authRoles = new HashSet<>();    public AuthUser() {    }    public AuthUser(String id, String fullName, String email, String username, String password, boolean accountNonExpired, boolean accountNonLocked, boolean credentialNonExpired, boolean enabled, Instant createdAt, Instant updatedAt, Set<AuthRole> authRoles) {        this.id = id;        this.fullName = fullName;        this.email = email;        this.username = username;        this.password = password;        this.accountNonExpired = accountNonExpired;        this.accountNonLocked = accountNonLocked;        this.credentialNonExpired = credentialNonExpired;        this.enabled = enabled;        this.createdAt = createdAt;        this.updatedAt = updatedAt;        this.authRoles = authRoles;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getFullName() {        return fullName;    }    public void setFullName(String fullName) {        this.fullName = fullName;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public boolean isAccountNonExpired() {        return accountNonExpired;    }    public void setAccountNonExpired(boolean accountNonExpired) {        this.accountNonExpired = accountNonExpired;    }    public boolean isAccountNonLocked() {        return accountNonLocked;    }    public void setAccountNonLocked(boolean accountNonLocked) {        this.accountNonLocked = accountNonLocked;    }    public boolean isCredentialNonExpired() {        return credentialNonExpired;    }    public void setCredentialNonExpired(boolean credentialNonExpired) {        this.credentialNonExpired = credentialNonExpired;    }    public boolean isEnabled() {        return enabled;    }    public void setEnabled(boolean enabled) {        this.enabled = enabled;    }    public Instant getCreatedAt() {        return createdAt;    }    public void setCreatedAt(Instant createdAt) {        this.createdAt = createdAt;    }    public Instant getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(Instant updatedAt) {        this.updatedAt = updatedAt;    }    public Set<AuthRole> getAuthRoles() {        return authRoles;    }    public void setAuthRoles(Set<AuthRole> authRoles) {        this.authRoles = authRoles;    }}