package com.sajansthapit.authserver.authuser.service;import com.sajansthapit.authserver.authuser.model.AuthRole;import java.util.List;public interface IAuthRoleService {    /**     * Method to save initial roles to database located in setting.json file     * @param roles: List<String>     */    void bootstrapRoles(List<String> roles);    /**     * Method to get AuthRole entity by role name     * @param name: String     * @return AuthRole entity or else throws EntityNotFoundException     */    AuthRole getByName(String name);}