package com.sajansthapit.authserver.authuser.service;import com.sajansthapit.authserver.config.bootstrap.dto.BootstrapUserDto;public interface IAuthUserService {    /**     * Method to save the initial user information to database located in setting.json file     *     * @param bootstrapUserDto: BootstrapUserDto     *                          - fullName, email, username, password, roles(list)     */    void bootstrapUser(BootstrapUserDto bootstrapUserDto);    /**     * Method to return total number of users present in the database     * @return long value     */    long getTotalUser();}