package com.sajansthapit.authserver.authuser.service.bootstrap;import com.sajansthapit.authserver.authuser.model.AuthRole;import com.sajansthapit.authserver.authuser.repository.AuthRoleRepository;import com.sajansthapit.authserver.config.bootstrap.service.BootstrapData;import org.springframework.stereotype.Service;import java.util.List;import java.util.UUID;@Servicepublic class AuthRoleBootstrapService implements BootstrapData<List<String>> {    private final AuthRoleRepository authRoleRepository;    public AuthRoleBootstrapService(AuthRoleRepository authRoleRepository) {        this.authRoleRepository = authRoleRepository;    }    @Override    public void bootstrap(List<String> roles) {        roles.forEach(role -> {            AuthRole authRole = new AuthRole();            authRole.setId(UUID.randomUUID().toString());            authRole.setName(role);            authRoleRepository.save(authRole);        });    }}