package com.sajansthapit.authserver.authuser.service.impl;import com.sajansthapit.authserver.authuser.model.AuthRole;import com.sajansthapit.authserver.authuser.repository.AuthRoleRepository;import com.sajansthapit.authserver.authuser.service.IAuthRoleService;import jakarta.persistence.EntityNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class AuthRoleService implements IAuthRoleService {    private final AuthRoleRepository authRoleRepository;    public AuthRoleService(AuthRoleRepository authRoleRepository) {        this.authRoleRepository = authRoleRepository;    }    @Override    public AuthRole getByName(String name) {        return authRoleRepository.findByName(name)                .orElseThrow(() -> new EntityNotFoundException("Role with name " + name + " not found"));    }}