package com.sajansthapit.authserver.authuser.service.impl;import com.sajansthapit.authserver.authuser.model.AuthRole;import com.sajansthapit.authserver.authuser.repository.AuthRoleRepository;import com.sajansthapit.authserver.authuser.service.IAuthRoleService;import jakarta.persistence.EntityNotFoundException;import org.springframework.stereotype.Service;import java.util.List;import java.util.UUID;@Servicepublic class AuthRoleService implements IAuthRoleService {    private final AuthRoleRepository authRoleRepository;    public AuthRoleService(AuthRoleRepository authRoleRepository) {        this.authRoleRepository = authRoleRepository;    }    @Override    public void bootstrapRoles(List<String> roles) {        roles.forEach(role -> {            AuthRole authRole = new AuthRole();            authRole.setId(UUID.randomUUID().toString());            authRole.setName(role);            authRoleRepository.save(authRole);        });    }    @Override    public AuthRole getByName(String name) {        return authRoleRepository.findByName(name)                .orElseThrow(() -> new EntityNotFoundException("Role with name " + name + " not found"));    }}