package com.sajansthapit.authserver.config.bootstrap;import com.google.gson.Gson;import com.sajansthapit.authserver.config.bootstrap.dto.BootstrapSettingDto;import com.sajansthapit.authserver.config.bootstrap.service.ProjectBootstrapService;import org.springframework.beans.factory.annotation.Value;import org.springframework.boot.CommandLineRunner;import org.springframework.core.io.Resource;import org.springframework.stereotype.Component;import java.nio.file.Files;@Componentpublic class ProjectBootstrapConfig implements CommandLineRunner {    @Value("classpath:setting.json")    Resource resourceFile;    private final ProjectBootstrapService projectBootstrapService;    public ProjectBootstrapConfig(ProjectBootstrapService projectBootstrapService) {        this.projectBootstrapService = projectBootstrapService;    }    @Override    public void run(String... args) throws Exception {        String data = new String(Files.readAllBytes(resourceFile.getFile().toPath()));        BootstrapSettingDto settingDto = new Gson().fromJson(data, BootstrapSettingDto.class);        projectBootstrapService.bootstrapUserData(settingDto);    }}