package com.sajansthapit.authserver.config.bootstrap.dto;import java.util.List;public class BootstrapClientDto {    private List<String> scopes;    private List<String> authorizationGrantTypes;    private List<String> authenticationMethods;    public BootstrapClientDto() {    }    public BootstrapClientDto(List<String> scopes, List<String> authorizationGrantTypes, List<String> authenticationMethods) {        this.scopes = scopes;        this.authorizationGrantTypes = authorizationGrantTypes;        this.authenticationMethods = authenticationMethods;    }    public List<String> getScopes() {        return scopes;    }    public void setScopes(List<String> scopes) {        this.scopes = scopes;    }    public List<String> getAuthorizationGrantTypes() {        return authorizationGrantTypes;    }    public void setAuthorizationGrantTypes(List<String> authorizationGrantTypes) {        this.authorizationGrantTypes = authorizationGrantTypes;    }    public List<String> getAuthenticationMethods() {        return authenticationMethods;    }    public void setAuthenticationMethods(List<String> authenticationMethods) {        this.authenticationMethods = authenticationMethods;    }}