package com.sajansthapit.authserver.config.bootstrap.service;import com.sajansthapit.authserver.authclient.service.bootstrap.AuthClientBootstrapService;import com.sajansthapit.authserver.authclient.service.bootstrap.AuthenticationMethodBootstrapService;import com.sajansthapit.authserver.authclient.service.bootstrap.AuthorizationGrantTypeBootstrapService;import com.sajansthapit.authserver.authclient.service.bootstrap.ScopeBootstrapService;import com.sajansthapit.authserver.authuser.service.bootstrap.AuthRoleBootstrapService;import com.sajansthapit.authserver.authuser.service.bootstrap.AuthUserBootstrapService;import com.sajansthapit.authserver.config.bootstrap.dto.BootstrapSettingDto;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Servicepublic class ProjectBootstrapService {    private final AuthUserBootstrapService authUserBootstrapService;    private final AuthRoleBootstrapService authRoleBootstrapService;    private final AuthClientBootstrapService authClientBootstrapService;    private final AuthenticationMethodBootstrapService authenticationMethodBootstrapService;    private final AuthorizationGrantTypeBootstrapService authorizationGrantTypeBootstrapService;    private final ScopeBootstrapService scopeBootstrapService;    public ProjectBootstrapService(AuthUserBootstrapService authUserBootstrapService, AuthRoleBootstrapService authRoleBootstrapService, AuthClientBootstrapService authClientBootstrapService, AuthenticationMethodBootstrapService authenticationMethodBootstrapService, AuthorizationGrantTypeBootstrapService authorizationGrantTypeBootstrapService, ScopeBootstrapService scopeBootstrapService) {        this.authUserBootstrapService = authUserBootstrapService;        this.authRoleBootstrapService = authRoleBootstrapService;        this.authClientBootstrapService = authClientBootstrapService;        this.authenticationMethodBootstrapService = authenticationMethodBootstrapService;        this.authorizationGrantTypeBootstrapService = authorizationGrantTypeBootstrapService;        this.scopeBootstrapService = scopeBootstrapService;    }    @Transactional    public void bootstrapUserData(BootstrapSettingDto bootstrapSettingDto){        if(authUserBootstrapService.getTotalUser() == 0){            authRoleBootstrapService.bootstrap(bootstrapSettingDto.getRoles());            authUserBootstrapService.bootstrap(bootstrapSettingDto.getUser());            authenticationMethodBootstrapService.bootstrap(bootstrapSettingDto.getClient().getAuthenticationMethods());            authorizationGrantTypeBootstrapService.bootstrap(bootstrapSettingDto.getClient().getAuthorizationGrantTypes());            scopeBootstrapService.bootstrap(bootstrapSettingDto.getClient().getScopes());            authClientBootstrapService.bootstrap(bootstrapSettingDto.getDefaultClient());        }    }}