package com.sajansthapit.authserver.config.bootstrap.service;import com.sajansthapit.authserver.authclient.service.bootstrap.AuthClientBootstrapService;import com.sajansthapit.authserver.authclient.service.bootstrap.AuthenticationMethodBootstrapService;import com.sajansthapit.authserver.authclient.service.bootstrap.AuthorizationGrantTypeBootstrapService;import com.sajansthapit.authserver.authclient.service.bootstrap.ScopeBootstrapService;import com.sajansthapit.authserver.authuser.service.IAuthRoleService;import com.sajansthapit.authserver.authuser.service.IAuthUserService;import com.sajansthapit.authserver.config.bootstrap.dto.BootstrapSettingDto;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Servicepublic class ProjectBootstrapService {    private final IAuthUserService authUserService;    private final IAuthRoleService authRoleService;    private final AuthClientBootstrapService authClientBootstrapService;    private final AuthenticationMethodBootstrapService authenticationMethodBootstrapService;    private final AuthorizationGrantTypeBootstrapService authorizationGrantTypeBootstrapService;    private final ScopeBootstrapService scopeBootstrapService;    public ProjectBootstrapService(IAuthUserService authUserService, IAuthRoleService authRoleService, AuthClientBootstrapService authClientBootstrapService, AuthenticationMethodBootstrapService authenticationMethodBootstrapService, AuthorizationGrantTypeBootstrapService authorizationGrantTypeBootstrapService, ScopeBootstrapService scopeBootstrapService) {        this.authUserService = authUserService;        this.authRoleService = authRoleService;        this.authClientBootstrapService = authClientBootstrapService;        this.authenticationMethodBootstrapService = authenticationMethodBootstrapService;        this.authorizationGrantTypeBootstrapService = authorizationGrantTypeBootstrapService;        this.scopeBootstrapService = scopeBootstrapService;    }    @Transactional    public void bootstrapUserData(BootstrapSettingDto bootstrapSettingDto){        if(authUserService.getTotalUser() == 0){            authRoleService.bootstrapRoles(bootstrapSettingDto.getRoles());            authUserService.bootstrapUser(bootstrapSettingDto.getUser());            authenticationMethodBootstrapService.bootstrap(bootstrapSettingDto.getClient().getAuthenticationMethods());            authorizationGrantTypeBootstrapService.bootstrap(bootstrapSettingDto.getClient().getAuthorizationGrantTypes());            scopeBootstrapService.bootstrap(bootstrapSettingDto.getClient().getScopes());            authClientBootstrapService.bootstrap(bootstrapSettingDto.getDefaultClient());        }    }}