package com.sajansthapit.authserver.config.security;import com.fasterxml.jackson.annotation.JsonIgnore;import com.sajansthapit.authserver.authuser.model.AuthUser;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.authority.SimpleGrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.util.Collection;import java.util.List;import java.util.stream.Collectors;public class AuthUserDetail implements UserDetails {    private final String id;    private final String username;    private final String email;    @JsonIgnore    private final String password;    private final Collection<? extends GrantedAuthority> authorities;    public AuthUserDetail(String id, String username, String email, String password, Collection<? extends GrantedAuthority> authorities) {        this.id = id;        this.username = username;        this.email = email;        this.password = password;        this.authorities = authorities;    }    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return authorities;    }    @Override    public String getPassword() {        return password;    }    @Override    public String getUsername() {        return username;    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return true;    }    public String id() {        return id;    }    public String getEmail() {        return email;    }    public static AuthUserDetail build(AuthUser authUser) {        List<GrantedAuthority> authorities = authUser.getAuthRoles().stream()                .map(role -> new SimpleGrantedAuthority(role.getName()))                .collect(Collectors.toList());        return new AuthUserDetail(authUser.getId(), authUser.getUsername(), authUser.getEmail()                , authUser.getPassword(), authorities);    }}